mapper.select:
select *
from user u
where userid = #{userID}

select user_type
from user
where id = #{id}

select u.id id,userid,user_name,tel_phone,district_name,community_name
from user u,user_profile p,community_info c,district d
where u.profile_id = p.id and p.community_id = c.id and d.id = c.district_id and u.user_type != '1'

    and community_name = #{community}


    and user_name = #{userName}

mapper.update:
update user
set user.user_password=#{newPassword}
where user.id = #{id}

update user
set user_type = '1'
where userid = #{userID}

update user
set user_type = '0'
where userid = #{userID}
